import { useEffect, useState } from 'react';
import axios from 'axios';

import Header from './Header';
import Footer from './Footer';
// import DriverDropdown from './DriverDropdown';
import YearDropdown from './YearDropdown';
import RaceDropdown from './RaceDropdown';

function App() {
  const baseUrl = 'http://127.0.0.1:8000/visualizations/';

  const [driver1, setDriver1] = useState('');
  const [driver2, setDriver2] = useState('');
  const [race, setRace] = useState('');
  const [year, setYear] = useState(1);

  const [races, setRaces] = useState([]);
  const [drivers, setDrivers] = useState([]);

  // const [image, setImage] = useState('');
  // const [positionImage, setPositionImage] = useState('');

  const handleYearSelect = async (e) => {
    setYear(e);
    axios.get(baseUrl + `races/${year}/`)
      .then(response => {
        if (response.data) {
          let obj = JSON.parse(response.data);
          let res = [];

          for (const key in obj) {
            res.push(
              {
                id: Number(key),
                label: obj[key],
                value: obj[key]
              }
            );
          }
          setRaces(res);
          console.log(races);
        }
      })
      .catch(error => {
        setRaces([]);
        console.error('Error fetching the races:', error);
      });;
  };

  const handleDriverSelect = async (e) => {
    setRace(e);
    axios.get(baseUrl + `drivers/${year}/${race}/`)
      .then(response => {
        if (response.data) {
          let obj = JSON.parse(response.data);
          let res = []
        }
      })
      .catch(error => {
        setDrivers([]);
        console.error('Error fetching the drivers:', error);
      });;
  };

  return (
    <div className="App">
      <Header />
      <div className='intro'>
        <p>Welcome to F1 Driver Comparison! With this app, you can compare two drivers head-to-head with past statistics and telemetry data.</p>
      </div>

      <div className='year'>
        <YearDropdown onSelect={handleYearSelect} />
      </div>

      if (year !== 1) {
        <div className='race'>
          <RaceDropdown dropdownOptions={races} onSelect={handleDriverSelect} />
        </div>
      }

      {/* <div className="content">
        <div className='driver1'>
          <div className='input1'>
