import { useState } from 'react';

import Header from './Header'
import Footer from './Footer'
import Dropdown from './Dropdown'

function App() {
  const validDrivers = ["Lewis Hamilton", "Max Verstappen"];

  const [driver1, setDriver1] = useState('');
  const [driver2, setDriver2] = useState('');
  // const [driverName1, setDriverName1] = useState('');
  // const [driverName2, setDriverName2] = useState('');
  // const [displayDriver1, setDisplayDriver1] = useState('');
  // const [displayDriver2, setDisplayDriver2] = useState('');

  // const handleClick = () => {
  //   setDisplayDriver1(require("./driver-images/" + driver1 + ".avif"));
  //   setDisplayDriver2(require("./driver-images/" + driver2 + ".avif"));
  // }

  return (
    <div className="App">
      <Header />
      <div className="content">
        <div className='intro'>
          <p>Welcome to F1 Driver Comparison! With this app, you can compare two drivers head-to-head with past statistics and real-time telemetry.</p>
        </div>

        <div className='input1'>
          <Dropdown onSelect={setDriver1} />
        </div>
        <div className='input2'>
          <Dropdown onSelect={setDriver2} />
        </div>

        {/* <div className='button'>
          <button onClick={() => handleClick()}>Compare</button>
        </div> */}

        <div className='image1'>
          {drive1.length > 0 &&
            <img src={"./driver-images/" + driver1 + ".avif"} />
          }
        </div>

        <div className='image2'>
          {driver2.length > 0 &&
            <img src={"./driver-images/" + driver2 + ".avif"} />
          }
        </div>
      </div>
      <Footer />
    </div>
  );

}

export default App;
